mapper.select:
SELECT *
FROM syllabletibet t
WHERE t.DID = #{did} 

AND t.onsetText IN
	
		#{queryType}
	


AND t.finalText IN
	
		#{queryType}
	


AND t.onsetWilleText IN
	
		#{queryType}
	


AND t.finalWilleText IN
	
		#{queryType}

SELECT onsetWilleText as willeText, orignRepresentationText, orignWltranscriptionText, GROUP_CONCAT(DID, ':', onsetText, ':', transcriptionText) as dialectText, translationText FROM syllabletibet WHERE DID IN
#{did}
AND onsetWilleText IN
#{queryItem}
GROUP BY onsetWilleText, orignRepresentationText, orignWltranscriptionText, translationText;

SELECT finalWilleText as willeText, orignRepresentationText, orignWltranscriptionText, GROUP_CONCAT(DID, ':', finalText, ':', transcriptionText) as dialectText, translationText FROM syllabletibet WHERE DID IN
#{did}
AND finalWilleText IN
#{queryItem}
GROUP BY finalWilleText, orignRepresentationText, orignWltranscriptionText, translationText;
